#include <iostream>
#include <time.h>
#include <iomanip>
#include "FHsort.h"
using namespace std;

// quickSortX internal, derived from quickSort from FHVector.h.
template <typename Comparable>
void quickSortX(FHvector<Comparable> &a, int left, int right, int recLimit)
{
	Comparable pivot;
	int i, j;
	if (left + recLimit <= right)
	{
		pivot = median3(a, left, right);
		for (i = left, j = right - 1; ; )
		{
			while (a[++i] < pivot)
				;
			while (pivot < a[--j])
				;
			if (i < j)
				mySwapFH(a[i], a[j]);
			else
				break;
		}
		mySwapFH(a[i], a[right - 1]);  // restore pivot
		// recursive calls on smaller sub-groups
		quickSortX(a, left, i - 1, recLimit);
		quickSortX(a, i + 1, right, recLimit);
	}
	else
		// non-recursive escape valve - insertionSort
		insertionSort(a, left, right);
}

// quickSortX public driver, derived from quickSort from FHVector.h.
template <typename Comparable>
void quickSortX(FHvector<Comparable> &a, int recLimit)
{
	if (recLimit >= 2)
		quickSortX(a, 0, a.size() - 1, recLimit);
	else
		cout << "recLimit must be >= 2.";
}

int main()
{
	srand(time(NULL));
	clock_t startTime, stopTime; 
	// Using 6 vectors, as in Assignment 7.
	// 150 2-D vectors due to the limit (300) and step (2).
	FHvector<int> fhVectorOfInts[6][150];
	// Sizes from from 40,000 to 250,000
	for (int i = 0; i < 40000; i++)
		fhVectorOfInts[0][0].push_back(rand());
	for (int i = 0; i < 50000; i++)
		fhVectorOfInts[1][0].push_back(rand());
	for (int i = 0; i < 100000; i++)
		fhVectorOfInts[2][0].push_back(rand());
	for (int i = 0; i < 200000; i++)
		fhVectorOfInts[3][0].push_back(rand());
	for (int i = 0; i < 249999; i++)
		fhVectorOfInts[4][0].push_back(rand());
	for (int i = 0; i < 250000; i++)
		fhVectorOfInts[5][0].push_back(rand());
	// Copying the randomly generated vectors onto the remaining 149 vectors.
	for (int i = 0; i < 6; i++)
		for (int k = 0; k < 150; k++)
			fhVectorOfInts[i][k] = fhVectorOfInts[i][0];
	// Timing the 150 quickSortX() calls on each fhVectorOfInts[i].
	double timeVector[6][150];
	for (int i = 0; i < 6; i++)
		for (int k = 0, j = 2; k < 150; k++, j = j + 2)
		{
			startTime = clock();
			quickSortX(fhVectorOfInts[i][k], j);
			stopTime = clock();
			timeVector[i][k] =
				(double)(stopTime - startTime) / (double)CLOCKS_PER_SEC;
		}
	// Output.
	cout << "Time complexity for quickSortX(), with even steps from 2 to 300, "
		<< "on 6 matrices containing 150 vectors of size:\n";
	cout << "Time legend: (~) = No change; (+) = Increase; (-) = Decrease\n\n";
	cout << setw(12) << "Step/Size" << setw(6) << '|';
	for (int i = 0; i < 6; i++)
		cout << setw(12) << fhVectorOfInts[i][0].size() << setw(6) << '|';
	cout << '\n';
	char change;
	for (int i = 0, j = 2; i < 150; i++, j = j + 2)
	{
		cout << setw(12) << j << setw(6) << '|';
		for (int k = 0; k < 6; k++)
		{
			if (!i | !(timeVector[k][i] - timeVector[k][i - 1]))
				change = '~';
			else if (0 > (timeVector[k][i] - timeVector[k][i - 1]))
				change = '-';
			else
				change = '+';
			cout << setw(12) << timeVector[k][i] << " (" << change << ") |";
		}
		cout << '\n';
	}
}

